import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const electron = window.require('electron');
const ipcRenderer = electron.ipcRenderer;

const Discord = require('discord.js');
const client = new Discord.Client();
const auth = require('./auth.json');

client.login(auth.token);

client.on('ready', () => {
    console.log(`\n\n\nLogged in as ${client.user.tag}!`);
    //listGuilds();
});

client.on('message', msg => {
	globalPage.setState({
		messages:globalPage.state.messages.concat("Hey"),
	});
});

function addMessage(){
	globalPage.setState({
		messages:globalPage.state.messages.concat("hey"),
	});
	console.log(globalPage.state.messages);
	globalPage.render();	
}

function createMessage(msg){
	return (<div><h1>{msg.member.nickname}</h1><p>{msg.content}</p></div>)
}
var globalPage;
class Page extends React.Component{
	constructor(props){
		super(props);
		this.state={
			guilds:["guilds"],
			channels:['channels'],
			messages:['messages'],
			members:['members']
		}
		globalPage=this;
	}
	render(){
		return (
			<div>
				<button onClick={()=>addMessage()}>Add</button>
				<button onClick={()=>this.showMessages()}>show</button>
				<Guilds guilds={this.state.guilds}/>
				<Channels channels={this.state.channels}/>
				<Messages messages={this.state.messages}/>
				<Members members={this.state.members}/>
			</div>
		)
	}
	showMessages(){
		console.log(this.state.messages);
	}
}

class Guilds extends React.Component{
	constructor(props){
		super(props);
		this.state={
			guilds:this.props.guilds,
		}
	}
	render(){
		return (
			<div id="guilds">
				{this.state.guilds}
			</div>
		)
	}
}

class Channels extends React.Component{
	constructor(props){
		super(props);
		this.state={
			channels:this.props.channels,
		}
	}
	render(){
		return (
			<div id="channels">
				{this.state.channels}
			</div>
		)
	}
}

class Messages extends React.Component{

	constructor(props){
		super(props);
		this.state={
			messages:this.props.messages,
		}
	}
	render(){
		return (
			<div id="messages">
				<button onClick={()=>this.showMessages()}>messsagebutt</button>
				{this.state.messages}
			</div>
		)
	}
	showMessages(){
		console.log(this.state.messages);
	}
	componentWillReceiveProps(props){
		this.setState({
			messages:props.messages
		})
	}
}

class Members extends React.Component{
	constructor(props){
		super(props);
		this.state={
			members:this.props.members,
		}
	}
	render(){
		return (
			<div id="members">
				{this.state.members}
			</div>
		)
	}

}

ReactDOM.render(
	<Page />,
	document.getElementById('root')
);
