import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const electron = window.require('electron');
const ipcRenderer = electron.ipcRenderer;

const Discord = require('discord.js');
const client = new Discord.Client();
const auth = require('./auth.json');

client.login(auth.token);

client.on('ready', () => {
    console.log(`\n\n\nLogged in as ${client.user.tag}!`);
    //listGuilds();
});

client.on('message', msg => {
	createMessage(msg);
});

function createMessage(msg){
	var div = <div><h1>{msg.member.nickname}</h1><p>{msg.content}</p></div>
	messagesDiv.addMessage (div);
}

var massage = <div><h1>Author</h1><p>content</p></div>;
var messagesDiv;


class Page extends React.Component{
	constructor(props){
		super(props);
		this.state={
			guilds:["guilds"],
			channels:['channels'],
			messages:['messages'],
			members:['members']
		}
	}
	render(){
		return (
			<div>
			<Guilds guilds={this.state.guilds}/>
			<Channels channels={this.state.channels}/>
			<Messages messages={this.state.messages}/>
			<Members members={this.state.members}/>
			</div>
		)
	}
}

function listGuilds(){
	var names=client.guilds.array().map(x=>{
		return x.name;
	});
	console.log(names);
}
class Guilds extends React.Component{
	constructor(props){
		super(props);
		this.state={
			guilds:this.props.guilds,
		}
	}
	render(){
		return (
			<div id="guilds">
				{this.state.guilds}
			</div>
		)
	}
}
class Channels extends React.Component{
	constructor(props){
		super(props);
		this.state={
			channels:this.props.channels,
		}
	}
	render(){
		return (
			<div id="channels">
				{this.state.channels}
			</div>
		)
	}
}
class Messages extends React.Component{

	constructor(props){
		super(props);
		this.state={
			messages:this.props.messages,
		}
		messagesDiv=this;
	}
	render(){
		return (
			<div id="messages">
				{this.state.messages}
			</div>
		)
	}
}
class Members extends React.Component{
	constructor(props){
		super(props);
		this.state={
			members:this.props.members,
		}
	}
	render(){
		return (
			<div id="members">
				{this.state.members}
			</div>
		)
	}
}
ReactDOM.render(
	<Page />,
	document.getElementById('root')
);
