import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const electron = window.require('electron');
const ipcRenderer = electron.ipcRenderer;

const Discord = require('discord.js');
const client = new Discord.Client();
const auth = require('./auth.json');

client.login(auth.token);

client.on('ready', () => {
    console.log(`\n\n\nLogged in as ${client.user.tag}!`);
});

client.on('message', msg => {
	createMessage(msg);
});

function createMessage(msg){

	var author = msg.member.nickname;
	var content= msg.content;
	var div = <div><h1>{msg.member.nickname}</h1><p>{msg.content}</p></div>

	messagesDiv.addMessage (div);
}

var massage = <div><h1>Author</h1><p>content</p></div>;
var messagesDiv;


class Page extends React.Component{
	render(){
		return (
			<Messages />
			)
	}
}

class Messages extends React.Component{

	constructor(props){
		super(props);
		this.state={
			messages:[],
		}
		messagesDiv=this;
	}
	render(){
		return (
			<div class="messages">
				{this.state.messages}
			</div>
		)
	}
	addMessage(msg){
		this.setState({
			messages:this.state.messages.concat(msg),
		})
	}
}
ReactDOM.render(
	<Page />,
	document.getElementById('root')
);
