import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const electron = window.require('electron');
const ipcRenderer = electron.ipcRenderer;

const Discord = require('discord.js');
const client = new Discord.Client();
const auth = require('./auth.json');

client.login(auth.token);


var globalPage;
client.on('ready', () => {
    console.log(`\n\n\nLogged in as ${client.user.tag}!`);

   	globalPage.setState({
   		guilds:client.guilds.array().map(x=>{
   			return <img onClick={()=>clickGuild(x)} className="guildIcon" src={x.iconURL} alt={x.name}/>
   		}),
   		channels:client.channels.array().map(x=>{
   			if (x.type=='text'){
	   			return <div onClick={()=>clickChannel(x)}  className="channel">{x.name}</div>
	   		}
   		})
   	});
});

client.on('message', msg => {
	globalPage.setState({
		messages:globalPage.state.messages.concat(createMessage(msg)),
	});
});
client.on('channelCreate',channel=>{
	globalPage.setState({
		channels:globalPage.state.channels.concat(<div onClick={()=>clickChannel(channel)}  className="channel">{channel.name}</div>),
	})
})

function clickChannel(channel){
	if (channel.type!='text'){
		return;
	}

	var messages;
	channel.fetchMessages().then(msgs=>{
		globalPage.setState({
			messages:msgs.map(x=>{return createMessage(x)}).reverse()
		})
	});
}
function clickGuild(guild){
	globalPage.setState({
		channels:guild.channels.map(x=>{
			if (x.type=='text'){
	   			return <div onClick={()=>clickChannel(x)} className="channel">{x.name}</div>
	   		}
		})
	});
}
function createMessage(msg){
	var nameColor={
		color:'white',
		color:msg.member.displayHexColor,
	}

	var messageText=msg.cleanContent;

//	let match=messageText.match(/\*\*[^\*]+\*\*/)
//	if(match!=null){
//		match[0]="<b>"+match[0].slice(2,-2)+"</b>";
//		messageText=messageText.slice(0,match.index)+match[0]+messageText.slice(match.index+match[0].length);
//		//match=messageText.match(/\*\*[^\*]+\*\*/);
//	}

	return (<div className = "message">
				<div classname="messageAuthor">
					<div style={{float:'right'}}>{msg.author.tag}</div>
					<div style={nameColor}>{msg.member.displayName}</div>
					<input type="checkbox" onClick={selectMessage(msg)}></input>
				</div>
				<div className="messageText">{messageText}</div>
			</div>)
}
let selectedMessages= new Map();
function selectMessage(msg){
	selectedMessages.set(msg.id,msg);
}
function printMessages(){
	selectedMessages.forEach(printMsg);
	function printMsg(val, key){
		console.log(val.cleanContent);
	}
}
class Page extends React.Component{
	constructor(props){
		super(props);
		this.state={
			guilds:"guilds",
			channels:'channels',
			messages:'messages',
			members:'members'
		}
		globalPage=this;
	}

	render(){
		return (
			<div>
				<Guilds guilds={this.state.guilds}/>
				<Channels channels={this.state.channels}/>
				<Messages messages={this.state.messages}/>
				<Members members={this.state.members}/>
			</div>
		)
	}
	showMessages(){
		console.log(this.state.messages);
	}
}

class Guilds extends React.Component{
	constructor(props){
		super(props);
		this.state={
			guilds:this.props.guilds,
		}
	}
	componentWillReceiveProps(props){
		this.setState({
			guilds:props.guilds,
		})
	}
	render(){
		return (
			<div id="guilds">
				{this.state.guilds}
			</div>
		)
	}
}

class Channels extends React.Component{
	constructor(props){
		super(props);
		this.state={
			channels:this.props.channels,
		}
	}
	componentWillReceiveProps(props){
		this.setState({
			channels:props.channels,
		})
	}
	render(){
		return (
			<div id="channels">
				{this.state.channels}
			</div>
		)
	}
}

class Messages extends React.Component{

	constructor(props){
		super(props);
		this.state={
			messages:this.props.messages,
		}
	}
	componentWillReceiveProps(props){
		this.setState({
			messages:props.messages
		})
	}
	render(){
		return (
			<div id="messages">
				<button type="button" onClick={()=>printMessages()}>Click Me!</button>
				{this.state.messages}
			</div>
		)
	}
	showMessages(){
		console.log(this.state.messages);
	}
}

class Members extends React.Component{
	constructor(props){
		super(props);
		this.state={
			members:this.props.members,
		}
	}
	componentWillReceiveProps(props){
		this.setState({
			members:props.members
		})
	}
	render(){
		return (
			<div id="members">
				{this.state.members}
			</div>
		)
	}

}

ReactDOM.render(
	<Page />,
	document.getElementById('root')
);
